import java.util.Scanner; class Main { public static void main(String args[]) { int a,c,d,e,i; Scanner sc = new Scanner (System.in); a=sc.nextInt(); int b[]=new int[a]; for(i=0;i<a;i++) { b[i] = sc.nextInt(); } app(a,b); } public static void app(int k,int y[]) { int temp=0,tem=0,te=0,i; if(k==6) { for(i=0;i<k/2;i++) { temp=temp+y[i]; } for(i=k/2;i<k;i++) { tem=tem+y[i]; } if(tem==temp) System.out.println("Perfect Batch"); else System.out.println("Not a Perfect Batch"); } if(k==9) { for(i=0;i<3;i++) { temp=temp+y[i]; } for(i=3;i<6;i++) { tem=tem+y[i]; } for(i=6;i<9;i++) { te=te+y[i]; } if(tem==temp || temp== te) System.out.println("Perfect Batch"); else System.out.println("Not a Perfect Batch"); } } }